import React, { useEffect, useState } from 'react';
import Nav from './Nav';
import useFetchUser from './useFetchUser';
import axios from 'axios';
import './css/board.css';
import { useNavigate } from 'react-router-dom';

function FreeBoard() {
    const navigate = useNavigate();
    const { user } = useFetchUser();
    const [board, setBoard] = useState('');

    const time = (date) => {
        const formatTime = date.slice(0, 16);
        return formatTime;
    };

    useEffect(() => {
        const boardData = async () => {
            try {
                const response = await axios.get('/postdb/select');
                if (Array.isArray(response.data)) {
                    setBoard(response.data);
                    console.log(response.data);
                } else {
                    console.error('Expected an array but got:', response.data);
                }
            } catch (error) {
                console.error(error);
            }
        };

        boardData();
    }, []);

    return (
        <div className="container">
            <header>
                <Nav user={user} />
            </header>
            <body className="mainContainer">
                <table className="boardTable">
                    <thead>
                        <th className="no">번호</th>
                        <th className="title">제목</th>
                        <th className="witer">작성자</th>
                        <th className="regDate">날짜</th>
                        <th>좋아요</th>
                        <th>조회수</th>
                    </thead>

                    {/* <tbody>
                        {board.map((item, index) => (
                            <tr key={index}>
                                <td>{item?.no}</td>
                                <td
                                    className="title"
                                    onClick={() => navigate('/viewBoard', { state: { no: item?.no, board } })}
                                >
                                    {item?.title}
                                </td>
                                <td className="witer">{item?.writer}</td>
                                <td className="regDate">{time(item?.regDate)}</td>
                                <td className="content">{item?.liked}</td>
                                <td className="content">{item?.hits}</td>
                            </tr>
                        ))}
                    </tbody> */}
                </table>
            </body>
        </div>
    );
}

export default FreeBoard;
