import React, { useEffect, useState } from 'react';
import Nav from './Nav';
import useFetchUser from './useFetchUser';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

function ViewBoard() {
    const { user } = useFetchUser();
    const location = useLocation();
    const no = { ...location.state };
    const [view, setView] = useState([]);

    const liked = async () => {
        try {
            const response = await axios.post('/postdb/liked', { post_no: no.no }, { withCredentials: true });
        } catch (error) {
            console.error(error);
        }
    };

    useEffect(() => {
        const viewData = async () => {
            try {
                const response = await axios.post('/postdb/view', { no: no.no });
                const data = response.data;
                setView(response.data);
                console.log(data);
            } catch (error) {
                console.error(error);
            }
        };
        viewData();
    }, []);

    return (
        <div>
            <body>
                <div>
                    <p>{view.title}</p>
                </div>
                <div>
                    <p>{view.writer}</p>
                </div>
                <div>
                    <p>{view.content}</p>
                </div>
                <div>
                    <p>{view.regDate}</p>
                </div>
                <div>
                    <p>{view.hits}</p>
                </div>
            </body>
            <p>{view.liked}</p>
            <a onClick={liked}>좋아요</a>
        </div>
    );
}

export default ViewBoard;
