import React, { useEffect } from 'react';
import './css/topBar.css';
import axios from 'axios';

function Nav({ user }) {
    useEffect(() => {}, [user]);

    const logout = async () => {
        try {
            const response = await axios.post('/userdb/logout');
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <header className="header">
            <div className="topContainer">
                <div className="title">
                    <h1>
                        <a href="/">정공해요</a>
                    </h1>
                    <div className="main-top-nav">
                        <a className="top-nav-text" href="/freeBoard">
                            자유게시판
                        </a>
                        <a className="top-nav-text" href="/">
                            장르별 게시판
                        </a>
                        <a className="top-nav-text" href="/">
                            음원 검색
                        </a>
                        <a className="top-nav-text" href="/">
                            공지사항
                        </a>
                    </div>
                </div>
                <div className="top-bar">
                    {user ? (
                        <div>
                            <a onClick={logout}>로그아웃</a>
                            <a>마이페이지</a>
                            <a>{user.name}님</a>
                        </div>
                    ) : (
                        <div>
                            <a className="login" href="/login">
                                로그인
                            </a>
                            <a className="signup" href="/signup">
                                회원가입
                            </a>
                        </div>
                    )}
                </div>
            </div>
        </header>
    );
}

export default Nav;
