import React, { useEffect } from 'react';
import Nav from './Nav';
import useFetchUser from './useFetchUser';
import axios from 'axios';

function ViewBoard({ route }) {
    const { user } = useFetchUser();
    const { no } = route.params;
    const [view, setView] = useState([]);

    useEffect(() => {
        const viewData = async () => {
            try {
                const response = await axios.post('/postdb/view', { no: no });
                const data = response.data;
                setView(data);
            } catch (error) {
                console.error(error);
            }
        };
        viewData();
    }, []);

    return (
        <div>
            <header>
                <Nav user={user} />
            </header>
            <body>
                <div>
                    <p>{view.title}</p>
                </div>
                <div>
                    <p>{view.writer}</p>
                </div>
                <div>
                    <p>{view.content}</p>
                </div>
                <div>
                    <p>{view.regDate}</p>
                </div>
                <div>
                    <p>{view.hits}</p>
                </div>
            </body>
        </div>
    );
}

export default ViewBoard;
