import React, { useEffect, useState } from 'react';
import Nav from './Nav';
import useFetchUser from './useFetchUser';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';
import { SERVER_ADDRESS } from './serverAddress/serverAddress';

function ViewBoard() {
    const { user } = useFetchUser();
    const navigate = useNavigate();
    const location = useLocation();
    const no = location.state.no;
    const category_no = location.state.category_no;
    const [view, setView] = useState([]);
    const [isLiked, setIsLiked] = useState(false);
    const [comment, setComment] = useState('');

    useEffect(() => {
        const viewData = async () => {
            try {
                const response = await axios.get(`${SERVER_ADDRESS}/postdb/view`, {
                    params: {
                        post_no: no,
                        category_no: category_no,
                    },
                });
                const data = response.data;
                setView(data);
            } catch (error) {
                console.error(error);
            }
        };
        viewData();
    }, [no, category_no]);

    useEffect(() => {
        if (user) {
            const checkLiked = async () => {
                try {
                    const response = await axios.post(
                        `${SERVER_ADDRESS}/postdb/checkLiked`,
                        { post_no: no, category_no: category_no },
                        { withCredentials: true }
                    );
                    setIsLiked(response.data.liked);
                } catch (error) {
                    console.error(error);
                }
            };
            checkLiked();
        }
    }, [no, category_no]);

    const liked = async () => {
        try {
            if (!user) {
                alert('로그인을 해주세요.');
                navigate('/login', { state: { form: location.pathname, no: no, category_no: category_no } });
                return;
            } else {
                const response = await axios.post(
                    `${SERVER_ADDRESS}/postdb/liked`,
                    { post_no: no, category_no: category_no },
                    { withCredentials: true }
                );
                if (response.data.success) {
                    setView((prevView) => ({
                        ...prevView,
                        liked: isLiked ? prevView.liked - 1 : prevView.liked + 1,
                    }));
                    setIsLiked((prev) => !prev);
                }
            }
        } catch (error) {
            console.error(error);
        }
    };

    console.log(comment);

    return (
        <div>
            <div>
                <Nav user={user} />
            </div>
            <div>
                <div>
                    <p>{view.title}</p>
                </div>
                <div>
                    <p>{view.writer}</p>
                </div>
                <div>
                    <p>{view.content}</p>
                </div>
                <div>
                    <p>{view.regDate}</p>
                </div>
                <div>
                    <p>{view.hits}</p>
                </div>
                <div>
                    <p>{view.liked}</p>
                    <button onClick={liked}>{user && isLiked ? '좋아요 취소' : '좋아요'}</button>
                </div>

                <div>
                    <p>댓글</p>
                    <textarea
                        className="comment-input"
                        maxLength={20}
                        placeholder="내용을 입력해 주세요."
                        value={comment}
                        onChange={(e) => setComment(e.target.value)}
                    />
                    <button>등록</button>
                </div>
            </div>
        </div>
    );
}

export default ViewBoard;
