import React, { useEffect, useState } from 'react';
import Nav from './Nav';
import useFetchUser from './useFetchUser';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';
import { SERVER_ADDRESS } from './serverAddress/serverAddress';

function ViewBoard() {
    const { user } = useFetchUser();
    const navigate = useNavigate();
    const location = useLocation();
    const no = location.state.no;
    const category_no = location.state.category_no || '';
    const [view, setView] = useState([]);
    const [isLiked, setIsLiked] = useState(false);
    const [path, setPath] = useState('');
    const [reqData, setReqData] = useState([]);

    useEffect(() => {
        if (no && category_no) {
            setPath(`${SERVER_ADDRESS}/board_category/view`);
            setReqData(no, category_no);
        } else if (no) {
            setPath(`${SERVER_ADDRESS}/postdb/view`);
            setReqData(no);
        }

        const viewData = async () => {
            try {
                const response = await axios.get(path, { params: { no } });
                const data = response.data;
                setView(data);
                console.log(data);
            } catch (error) {
                console.error(error);
            }
        };
        viewData();
    }, [no]);

    useEffect(() => {
        const checkLiked = async () => {
            try {
                const response = await axios.post(
                    `${SERVER_ADDRESS}/postdb/checkLiked`,
                    { post_no: no },
                    { withCredentials: true }
                );
                setIsLiked(response.data.liked);
            } catch (error) {
                console.error(error);
            }
        };
        checkLiked();
    }, [no]);

    const liked = async () => {
        try {
            if (user) {
                const response = await axios.post(
                    `${SERVER_ADDRESS}/postdb/liked`,
                    { post_no: no },
                    { withCredentials: true }
                );
                if (response.data.success) {
                    setView((prevView) => ({
                        ...prevView,
                        liked: isLiked ? prevView.liked - 1 : prevView.liked + 1,
                    }));
                    setIsLiked((prev) => !prev);
                }
            } else {
                alert('로그인을 해주세요.');
                navigate('/login', { state: { form: '/freeboard' } });
                return;
            }
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div>
            <div>
                <Nav user={user} />
            </div>
            <div>
                <p>{view.title}</p>
            </div>
            <div>
                <p>{view.writer}</p>
            </div>
            <div>
                <p>{view.content}</p>
            </div>
            <div>
                <p>{view.regDate}</p>
            </div>
            <div>
                <p>{view.hits}</p>
            </div>

            <p>{view.liked}</p>
            <button onClick={liked}>{user && isLiked ? '좋아요 취소' : '좋아요'}</button>
        </div>
    );
}

export default ViewBoard;
