import React, { useState } from 'react';
import Nav from './Nav';
import useFetchUser from './useFetchUser';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

function WritePost() {
    const { user } = useFetchUser();
    const navigate = useNavigate();
    const location = useLocation();
    const no = location.state || {};
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [path, setPath] = useState('');

    const writePost = async () => {
        const requestBody = {
            title: title,
            content: content,
            writer: user.nickname,
        };
        if (no.no) {
            requestBody.category_no = no.no;
        }

        if (no) {
            setPath('http://localhost:8080/board_category/insertBoard');
        } else {
            setPath('http://localhost:8080/postdb/insert');
        }

        try {
            if (title === '') {
                alert('제목을 입력해 주세요.');
                return false;
            } else if (content === '') {
                alert('내용을 입력해 주세요.');
                return false;
            } else {
                const response = await axios.post(path, requestBody, {
                    withCredentials: true,
                });
                alert('게시글이 작성되었습니다!');
                navigate('/');
                return true;
            }
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div style={{ display: 'flex', flex: 1 }}>
            <header>
                <Nav user={user} />
            </header>

            <div>
                <div>
                    <input
                        placeholder="제목을 입력해 주세요."
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                    />
                </div>
                <div>
                    <p>작성자</p>
                    {user ? <p>{user.nickname}</p> : null}
                </div>
                <div>
                    <textarea
                        maxLength={1000}
                        placeholder="내용을 입력해 주세요."
                        value={content}
                        onChange={(e) => setContent(e.target.value)}
                    />
                    <p>{content.length} / 1000</p>
                </div>
                <button type="button" onClick={writePost}>
                    작성하기
                </button>
            </div>
        </div>
    );
}

export default WritePost;
