import React, { useEffect, useState } from 'react';
import Nav from './Nav';
import useFetchUser from './useFetchUser';
import { usePosts } from './context/postContext';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

function Category_posts() {
    const { user } = useFetchUser();
    const navigate = useNavigate();
    const location = useLocation();
    const { no, category } = location.state || {};
    const { posts, setPosts } = usePosts();
    const [boardData, setBoardData] = useState([]);

    const time = (date) => {
        const formatTime = date.slice(0, 16);
        return formatTime;
    };

    useEffect(() => {
        axios
            .get(`http://localhost:8080/board_category/selectBoard`, {
                params: {
                    category_no: no,
                },
            })
            .then((res) => {
                setBoardData(res.data);
                setPosts(res.data);
                console.log(res.data);
            })
            .catch((error) => {
                console.error(error);
            });
    }, [no]);

    const handleWritePost = () => {
        if (!user) {
            alert('로그인을 해주세요.');
            navigate('/login', { state: { path: location.pathname } });
        } else {
            navigate('/writePost');
        }
    };

    console.log(posts);

    return (
        <div>
            <header>
                <Nav user={user} />
            </header>
            <div>
                <div>
                    <p>{category}</p>
                </div>
                <div className="mainContainer">
                    <table className="boardTable">
                        <thead>
                            <tr>
                                <th className="no">번호</th>
                                <th className="titleName">제목</th>
                                <th className="witer">작성자</th>
                                <th className="regDate">날짜</th>
                                <th>좋아요</th>
                                <th>조회수</th>
                            </tr>
                        </thead>
                        <tbody>
                            {boardData.map((item, index) => (
                                <tr key={index}>
                                    <td>{item.no}</td>
                                    <td className="titleText">{item.title}</td>
                                    <td className="witer">{item.writer}</td>
                                    <td className="regDate">{time(item.regDate)}</td>
                                    <td className="content">{item.liked}</td>
                                    <td className="content">{item.hits}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <div>
                        <button onClick={handleWritePost}>게시글 작성</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Category_posts;
