import React, { useEffect, useState } from 'react';
import Nav from './Nav';
import { usePosts } from './context/postContext';
import useFetchUser from './useFetchUser';
import axios from 'axios';
import './css/board.css';
import { useLocation, useNavigate } from 'react-router-dom';

function FreeBoard() {
    const navigate = useNavigate();
    const location = useLocation();
    const { user } = useFetchUser();
    const { post, setPosts } = usePosts();
    const [board, setBoard] = useState([]);

    console.log(post);

    const time = (date) => {
        const formatTime = date.slice(0, 16);
        return formatTime;
    };

    const handleWritePost = () => {
        if (!user) {
            alert('로그인을 해주세요.');
            navigate('/login', { state: { path: location.pathname } });
        } else {
            navigate('/writePost');
        }
    };

    useEffect(() => {
        axios
            .get('http://localhost:8080/postdb/select')
            .then((res) => {
                setBoard(res.data);
                setPosts(res.data);
            })
            .catch((error) => console.error(error));
    }, []);

    return (
        <div className="container">
            <header>
                <Nav user={user} />
            </header>
            <div className="mainContainer">
                <table className="boardTable">
                    <thead>
                        <tr>
                            <th className="no">번호</th>
                            <th className="titleName">제목</th>
                            <th className="witer">작성자</th>
                            <th className="regDate">날짜</th>
                            <th>좋아요</th>
                            <th>조회수</th>
                        </tr>
                    </thead>
                    <tbody>
                        {board.map((item, index) => (
                            <tr key={index}>
                                <td>{item?.no}</td>
                                <td
                                    className="titleText"
                                    onClick={() => navigate('/viewBoard', { state: { no: item?.no } })}
                                >
                                    {item.title}
                                </td>
                                <td className="witer">{item?.writer}</td>
                                <td className="regDate">{time(item?.regDate)}</td>
                                <td className="content">{item?.liked}</td>
                                <td className="content">{item?.hits}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                <div>
                    <button type="button" onClick={handleWritePost}>
                        게시글 작성하기
                    </button>
                </div>
            </div>
        </div>
    );
}

export default FreeBoard;
