import React, { useEffect, useState } from 'react';
import './css/topBar.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Nav({ user }) {
    const navigate = useNavigate();
    const [category, setCategory] = useState([]);
    const [open, setOpen] = useState(false);

    useEffect(() => {
        axios
            .get('http://localhost:8080/posts_category/category')
            .then((res) => {
                setCategory(res.data);
                console.log(res.data);
            })
            .catch((error) => {
                console.error(error);
            });
    }, []);

    useEffect(() => {}, [user]);

    const logout = async () => {
        try {
            const response = await axios.post('http://localhost:8080/userdb/logout', {}, { withCredentials: true });
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <header className="header">
            <div className="topContainer">
                <div className="title">
                    <h1>
                        <a href="/">정공해요</a>
                    </h1>
                    <div className="main-top-nav">
                        <a className="top-nav-text" href="/freeBoard">
                            자유게시판
                        </a>
                        <div>
                            <a className="top-nav-text" onClick={() => setCategory((prev) => !prev)}>
                                장르별 게시판
                            </a>
                            <div>
                                {category.map((item, index) => (
                                    <p key={index} {...item.category}></p>
                                ))}
                            </div>
                        </div>
                        <a className="top-nav-text" href="/">
                            음원 검색
                        </a>
                        <a className="top-nav-text" href="/">
                            공지사항
                        </a>
                    </div>
                </div>
                <div className="top-bar">
                    {user ? (
                        <div>
                            <a onClick={logout} href="/">
                                로그아웃
                            </a>
                            <a>마이페이지</a>
                            <a>{user.name}님</a>
                        </div>
                    ) : (
                        <div>
                            <a className="login" href="/login">
                                로그인
                            </a>
                            <a className="signup" href="/signup">
                                회원가입
                            </a>
                        </div>
                    )}
                </div>
            </div>
        </header>
    );
}

export default Nav;
