import React, { useEffect, useState } from 'react';
import Nav from './Nav';
import useFetchUser from './useFetchUser';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

function ViewBoard() {
    const { user } = useFetchUser();
    const no = location.state.no;
    const navigate = useNavigate();
    const location = useLocation();
    const [view, setView] = useState([]);
    const [isLiked, setIsLiked] = useState(false);

    useEffect(() => {
        const checkLiked = async () => {
            try {
                const response = await axios.post(
                    'http://localhost:8080/postdb/checkLiked',
                    { post_no: no },
                    { withCredentials: true }
                );
                console.log(response.data.liked);
                setIsLiked(response.data.liked);
            } catch (error) {
                console.error(error);
            }
        };
        checkLiked();
    }, [no]);

    const liked = async () => {
        try {
            if (user) {
                const response = await axios.post(
                    'http://localhost:8080/postdb/liked',
                    { post_no: no },
                    { withCredentials: true }
                );
                if (response.data.success) {
                    setView((prevView) => ({
                        ...prevView,
                        liked: isLiked ? prevView.liked - 1 : prevView.liked + 1,
                    }));
                    setIsLiked((prev) => !prev);
                }
            } else {
                alert('로그인을 해주세요.');
                navigate('/login', { state: { form: location.pathname } });
                return;
            }
        } catch (error) {
            console.error(error);
        }
    };

    useEffect(() => {
        const no = location.state?.no;
        const viewedPost = JSON.parse(
            document.cookie
                .split('; ')
                .find((row) => row.startsWith('viewedPost='))
                ?.split('=')[1] || '[]'
        );

        if (!viewedPost.includes(no)) {
            const viewData = async () => {
                try {
                    const response = await axios.post('http://localhost:8080/postdb/view', { no: no });
                    const data = response.data;
                    setView(data);
                    setIsLiked(data.liked > 0);
                } catch (error) {
                    console.error(error);
                }
            };
            viewData();
        }
    }, [no]);

    return (
        <div>
            <div>
                <Nav user={user} />
            </div>
            <div>
                <p>{view.title}</p>
            </div>
            <div>
                <p>{view.writer}</p>
            </div>
            <div>
                <p>{view.content}</p>
            </div>
            <div>
                <p>{view.regDate}</p>
            </div>
            <div>
                <p>{view.hits}</p>
            </div>

            <p>{view.liked}</p>
            <button onClick={liked}>{user && isLiked ? '좋아요 취소' : '좋아요'}</button>
        </div>
    );
}

export default ViewBoard;
