import React, { useEffect, useState } from 'react';
import Nav from './Nav';
import useFetchUser from './useFetchUser';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

function ViewBoard() {
    const { user } = useFetchUser();
    const navigate = useNavigate();
    const location = useLocation();
    const no = { ...location.state };
    const [view, setView] = useState([]);
    const [isLiked, setIsLiked] = useState(false);

    const liked = async () => {
        try {
            if (user) {
                const response = await axios.post('/postdb/liked', { post_no: no.no }, { withCredentials: true });
                if (response.data.success) {
                    setView((prevView) => ({
                        ...prevView,
                        liked: isLiked ? prevView.liked - 1 : prevView.liked + 1,
                    }));
                    setIsLiked((prev) => !prev);
                } else {
                    alert('로그인을 해주세요.');
                    navigate('/login', { state: { form: location.pathname } });
                    return;
                }
            }
        } catch (error) {
            console.error(error);
        }
    };

    useEffect(() => {
        const viewData = async () => {
            try {
                const response = await axios.post('/postdb/view', { no: no.no });
                const data = response.data;
                setView(data);
                setIsLiked(data.liked > 0);
            } catch (error) {
                console.error(error);
            }
        };
        viewData();
    }, [no.no]);

    return (
        <div>
            <body>
                <div>
                    <p>{view.title}</p>
                </div>
                <div>
                    <p>{view.writer}</p>
                </div>
                <div>
                    <p>{view.content}</p>
                </div>
                <div>
                    <p>{view.regDate}</p>
                </div>
                <div>
                    <p>{view.hits}</p>
                </div>
            </body>
            <p>{view.liked}</p>
            <button onClick={liked}>{isLiked ? '좋아요 취소' : '좋아요'}</button>
        </div>
    );
}

export default ViewBoard;
