import React, { useEffect, useState } from 'react';
import Nav from './Nav';
import useFetchUser from './useFetchUser';
import axios from 'axios';

function FreeBoard() {
    const { user } = useFetchUser();
    const [board, setBoard] = useState([]);

    useEffect(() => {
        const boardData = async () => {
            try {
                const response = await axios.post('/postdb/select');
                setBoard(response.data);
                console.log(response.data);
            } catch (error) {
                console.error(error);
            }
        };
        boardData();
    }, []);

    return (
        <div>
            <header>
                <Nav user={user} />
            </header>
            <body>
                <div>
                    <div>
                        {board.map((item, index) => {
                            <div key={index}>
                                <p>{item.title}</p>
                                <p>{item.writer}</p>
                                <p>{item.content}</p>
                                <p>{item.regDate}</p>
                            </div>;
                        })}
                    </div>
                </div>
            </body>
        </div>
    );
}

export default FreeBoard;
